style:
html, body {
    height: 100%;
    margin: 0;
}
body {
    background: linear-gradient(45deg, #a8e063 0%, #56ab2f 100%);
    background-attachment: fixed;
    font-family: 'Inter', sans-serif;
}
.article-container {
    background-color: rgba(255, 255, 255, 0.8);
    border-radius: 8px;
    padding: 20px;
    margin: 20px auto;
    max-width: 800px;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
}
.article-title {
    font-size: 2rem;
    font-weight: bold;
    color: #333;
    margin-bottom: 10px;
}
.article-content {
    line-height: 1.6;
    color: #555;
}
